class DoctorBot:
      def __init__(self):
          self.symptoms = {
               “headache”: “You might have a migraine or tension headache. If it persists, please see a doctor.”,
               “fever”: “You might have an infection. Please monitor your temperature and see a doctor if it remains high.”,
               “cough”: “It could be a cold or flu. If it persists or is severe, please see a doctor.”
        }
    def check_symptom(self, symptom):
        return self.symptoms.get(symptom, “I’m not sure about that symptom. Please consult a doctor.”)
def main():
    bot = DoctorBot()
    while True:
       symptom = input(“Please describe your symptom: “).lower()
       if symptom == “exit”:
           break
       print(bot.check_symptom(symptom))
if __name__ == “__main__”:
     main()
